    dispatch(remove(item.id));
    toast.success("item removed")
  }



  return (
    <div>
<div>
  <div>
    <img src={item.image} />
  </div>
  <div>
    <h1>{item.title}</h1>
    <h1>{item.decsription}</h1>
  </div>
  <div>
   <p>{item.price}</p>
   <div onClick={removeFromCart}>
    <FcDeleteDatabase/>
   </div>
  </div>
</div>

    </div>
  )
}

