import React from "react";
import { FcDeleteDatabase } from "react-icons/fc";
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const CartItem = ({ item, itemIndex }) => {
  const dispatch = useDispatch();
  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("item removed");
  };

  return (
    <div className="">
      <div className="flex justify-between gap-10 mb-7 w-50">
        <div c>
          <img src={item.image} className="h-[150px]" />
        </div>
        <div className="">
          <h1 className="text-gray-700 font-semibold text-sm text-left  w-40 mt-1">
            {item.title}
          </h1>
          <h1 className="w-40 text-gray-400 font-normal text-[10px] text-left mt-3">
            {item.description.split(" ").slice(0, 15).join(" ") + "..."}
          </h1>
          <div className="mt-4 flex justify-between items-center">
            <p className="text-green-600 font-semibold">{item.price}</p>
            <div onClick={removeFromCart}>
              <FcDeleteDatabase />
     
            </div>
               
          </div>
              
        </div>
           
      </div>
      <div className="border border-b-gray-500" ></div>
    </div>
  );
};

export default CartItem;
