import React from "react";
import { FcDeleteDatabase } from "react-icons/fc";
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const CartItem = ({ item, itemIndex }) => {
  const dispatch = useDispatch();
  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("item removed");
  };

  return (
    <div>
       <div className="flex gap-10 mb-7">
        <div >
          <img src={item.image} className="h-[150px]" />
        </div>
        <div >
          <h1 className='text-gray-700 font-semibold text-sm text-left  w-40 mt-1'>{item.title}</h1>
          <h1 className='w-40 text-gray-400 font-normal text-[10px] text-left'>{item.description.split(" ").slice(0,15).join(" ") + "..."}</h1>
          <div>
            <p className='text-green-600 font-semibold'>{item.price}</p>
            <div
            onClick={removeFromCart}>
              <FcDeleteDatabase/>
            </div>
          </div>

        </div>


      </div>
    </div>
  );
};

export default CartItem;
